import { getAdjustedPrice, getNextPrice } from "./../src/utils";

describe("utils", () => {
  it("get adjusted price", async () => {
    const price = 100;
    const fee = 3000;
    const adjustedPrice = getAdjustedPrice(price, fee);
    const nextPrice = getNextPrice(price, fee, 1);
    const prevPrice = getNextPrice(price, fee, -1);
    console.log({
      price,
      adjustedPrice,
      nextPrice,
      prevPrice,
    });
  });
});
